import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
  ComponentProps,
} from "streamlit-component-lib";
import React, { ReactNode } from "react";
import { PDFDocument } from "pdf-lib";

// Define the State interface
interface State {
  isProcessing: boolean;
  progress: number;
}

// Define the Props interface
interface Props extends ComponentProps {
  args: {
    imageData?: string;
    imageList?: Array<{ imageData: string; fileName: string }>;
    fileName?: string;
    dpi?: number;
    pageSize?: { width: number; height: number };
    bleed?: boolean;
    outputFileName?: string;
  };
}

class PDFComponent extends StreamlitComponentBase<Props, State> {
  private lastProcessedImageList: string | null;
  private lastProcessedSingleImage: string | null;

  constructor(props: Props) {
    super(props);
    this.state = {
      isProcessing: false,
      progress: 0,
    };
    this.lastProcessedImageList = null;
    this.lastProcessedSingleImage = null;
  }

  public render = (): ReactNode => {
    const { args } = this.props;
    const imageData = args.imageData; // For single image
    const fileName = args.fileName || "output.pdf"; // Single image file name
    const imageList = args.imageList;
    const dpi = args.dpi || 300;
    const pageSize = args.pageSize || { width: 8.5, height: 11 };
    const bleed = args.bleed || false;

    // Single image handling
    if (imageData && !this.state.isProcessing) {
      if (imageData !== this.lastProcessedSingleImage) {
        this.setState({ isProcessing: true, progress: 0 }, () => {
          this.lastProcessedSingleImage = imageData;
          this.convertSingleImageToPdf(imageData, fileName, dpi, pageSize, bleed);
        });
      }
    }
    // Multi-image handling
    else if (imageList && imageList.length > 0 && !this.state.isProcessing) {
      const serializedImageList = JSON.stringify(imageList); // Serialize to detect changes
      if (serializedImageList !== this.lastProcessedImageList) {
        this.setState({ isProcessing: true, progress: 0 }, () => {
          this.lastProcessedImageList = serializedImageList;
          this.convertImagesToSinglePdf(imageList, dpi, pageSize, bleed);
        });
      }
    }

    return (
      <div>
        {this.state.isProcessing && (
          <div>
            <p>Processing: {this.state.progress}%</p>
            <progress value={this.state.progress} max="100" />
          </div>
        )}
      </div>
    );
  };

  private convertSingleImageToPdf = async (
    imageData: string,
    fileName: string,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean
  ): Promise<void> => {
    try {
      const pdfDoc = await PDFDocument.create();
      this.setState({ progress: 25 });
      await this.addImageToPdf(pdfDoc, imageData, dpi, pageSize, bleed);
      this.setState({ progress: 50 });

      const pdfBytes = await pdfDoc.save();
      this.setState({ progress: 75 });
      this.triggerDownload(pdfBytes, fileName);
      this.setState({ progress: 100 });

      Streamlit.setComponentValue(true); // Indicate success
    } catch (error) {
      console.error("Error generating single PDF:", error);
      Streamlit.setComponentValue(false); // Indicate failure
    } finally {
      this.setState({ isProcessing: false }); // Reset processing state
    }
  };

  private convertImagesToSinglePdf = async (
    imageList: Array<{ imageData: string; fileName: string }>,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean
  ): Promise<void> => {
    try {
      const pdfDoc = await PDFDocument.create();

      const totalImages = imageList.length;
      let processedImages = 0;

      // Add all images to the PDF
      for (const { imageData } of imageList) {
        await this.addImageToPdf(pdfDoc, imageData, dpi, pageSize, bleed);
        processedImages += 1;
        const progress = Math.round((processedImages / totalImages) * 100);
        this.setState({ progress });
      }

      const pdfBytes = await pdfDoc.save();
      const fileName = this.props.args.outputFileName || "bulk_images.pdf";

      // Trigger the download
      this.triggerDownload(pdfBytes, fileName);

      Streamlit.setComponentValue(true); // Indicate success
    } catch (error) {
      console.error("Error generating bulk PDF:", error);
      Streamlit.setComponentValue(false); // Indicate failure
    } finally {
      this.setState({ isProcessing: false }); // Reset processing state
    }
  };

  private addImageToPdf = async (
    pdfDoc: PDFDocument,
    imageData: string,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean
  ): Promise<void> => {
    const imageBytes = Uint8Array.from(atob(imageData), (c) => c.charCodeAt(0));
    const embeddedImage = await pdfDoc.embedPng(imageBytes);

    const pageWidthInPoints = pageSize.width * 72;
    const pageHeightInPoints = pageSize.height * 72;

    let scale, scaledWidth, scaledHeight, xOffset, yOffset;

    if (bleed) {
      const widthRatio = pageWidthInPoints / embeddedImage.width;
      const heightRatio = pageHeightInPoints / embeddedImage.height;
      scale = Math.max(widthRatio, heightRatio);
      scaledWidth = embeddedImage.width * scale;
      scaledHeight = embeddedImage.height * scale;
      xOffset = (pageWidthInPoints - scaledWidth) / 2;
      yOffset = (pageHeightInPoints - scaledHeight) / 2;
    } else {
      const margin = 0.75 * 72;
      const contentWidth = pageWidthInPoints - 2 * margin;
      const contentHeight = pageHeightInPoints - 2 * margin;

      const widthRatio = contentWidth / embeddedImage.width;
      const heightRatio = contentHeight / embeddedImage.height;
      scale = Math.min(widthRatio, heightRatio);
      scaledWidth = embeddedImage.width * scale;
      scaledHeight = embeddedImage.height * scale;
      xOffset = (contentWidth - scaledWidth) / 2 + margin;
      yOffset = (contentHeight - scaledHeight) / 2 + margin;
    }

    const page = pdfDoc.addPage([pageWidthInPoints, pageHeightInPoints]);
    page.drawImage(embeddedImage, {
      x: xOffset,
      y: yOffset,
      width: scaledWidth,
      height: scaledHeight,
    });
  };

  private triggerDownload = (pdfBytes: Uint8Array, fileName: string): void => {
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const url = URL.createObjectURL(blob);

    const link = document.createElement("a");
    link.href = url;
    link.download = fileName;
    document.body.appendChild(link);

    link.click();
    document.body.removeChild(link);
  };
}

export default withStreamlitConnection(PDFComponent);
