import React, { useEffect } from "react";
import { Streamlit, withStreamlitConnection } from "streamlit-component-lib";

interface ScrollProps {
  args: {
    targetId?: string; // Optional ID of the element to scroll to
  };
}

const ScrollToTopComponent: React.FC<ScrollProps> = ({ args }) => {
  const { targetId } = args;

  useEffect(() => {
    const scrollTarget = targetId
      ? document.getElementById(targetId) // Look for the target in the current document
      : null;

    if (scrollTarget) {
      // Scroll to the specific element
      scrollTarget.scrollIntoView({ behavior: "smooth" });
    } else {
      // Scroll to the top of the page
      window.scrollTo({ top: 0, behavior: "smooth" });
    }

    // Notify Streamlit that the action is complete
    Streamlit.setComponentValue(true);
  }, [targetId]);

  return null; // No UI elements to render
};

export default withStreamlitConnection(ScrollToTopComponent);
