import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";
import React, { ReactNode } from "react";
import { PDFDocument } from "pdf-lib";

class PDFComponent extends StreamlitComponentBase {
  private isProcessing: boolean = false;
  private lastProcessedImageData: string | null = null;
  private lastProcessedFileName: string | null = null;

  private debounce = (func: Function, delay: number) => {
    let timeoutId: NodeJS.Timeout;
    return (...args: any[]) => {
      if (timeoutId) clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  private debouncedDownload = this.debounce((link: HTMLAnchorElement) => {
    link.click();
    document.body.removeChild(link);
  }, 300);

  private base64ToUint8Array(base64: string): Uint8Array {
    const binaryString = atob(base64);
    const length = binaryString.length;
    const bytes = new Uint8Array(length);
    for (let i = 0; i < length; i++) {
      bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes;
  }

  public render = (): ReactNode => {
    const imageData = this.props.args["imageData"] as string;
    const fileName = this.props.args["fileName"] || "output.pdf";
    const imageList = this.props.args["imageList"] as Array<{ imageData: string; fileName: string }>;
    const dpi = this.props.args["dpi"] || 300;
    const pageSize = this.props.args["pageSize"] || { width: 8.5, height: 11 };
    const bleed = this.props.args["bleed"] || false;

    if (!this.isProcessing) {
      if (imageData) {
        if (imageData !== this.lastProcessedImageData || fileName !== this.lastProcessedFileName) {
          this.isProcessing = true;
          this.lastProcessedImageData = imageData;
          this.lastProcessedFileName = fileName;
          this.convertSingleImageToPdf(imageData, fileName, dpi, pageSize, bleed);
        }
      } else if (imageList && imageList.length > 0) {
        this.isProcessing = true;
        this.convertImagesToSinglePdf(imageList, dpi, pageSize, bleed);
      }
    }

    return <div></div>;
  };

  private convertSingleImageToPdf = async (
    imageData: string,
    fileName: string,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean
  ): Promise<void> => {
    try {
      const pdfDoc = await PDFDocument.create();
      await this.addImageToPdf(pdfDoc, imageData, dpi, pageSize, bleed);

      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = url;
      link.download = fileName;
      document.body.appendChild(link);

      this.debouncedDownload(link);
      Streamlit.setComponentValue(true);
    } catch (error) {
      console.error("Error generating PDF:", error);
      Streamlit.setComponentValue(false);
    } finally {
      this.isProcessing = false;
    }
  };

  private convertImagesToSinglePdf = async (
    imageList: Array<{ imageData: string; fileName: string }>,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean
  ): Promise<void> => {
    try {
      const pdfDoc = await PDFDocument.create();

      for (const { imageData } of imageList) {
        await this.addImageToPdf(pdfDoc, imageData, dpi, pageSize, bleed);
      }

      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = url;
      link.download = "bulk_images.pdf";
      document.body.appendChild(link);

      this.debouncedDownload(link);
      Streamlit.setComponentValue(true);
    } catch (error) {
      console.error("Error generating single PDF:", error);
      Streamlit.setComponentValue(false);
    } finally {
      this.isProcessing = false;
    }
  };

  private addImageToPdf = async (
    pdfDoc: PDFDocument,
    imageData: string,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean
  ): Promise<void> => {
    const imageBytes = this.base64ToUint8Array(imageData);
    const embeddedImage = await pdfDoc.embedPng(imageBytes);

    const pageWidthInPoints = pageSize.width * 72;
    const pageHeightInPoints = pageSize.height * 72;

    const margin = bleed ? 0 : 0.75 * 72; // 0.75-inch margin in points
    const contentWidth = pageWidthInPoints - 2 * margin;
    const contentHeight = pageHeightInPoints - 2 * margin;

    const originalWidth = embeddedImage.width;
    const originalHeight = embeddedImage.height;

    let scale: number, scaledWidth: number, scaledHeight: number, xOffset: number, yOffset: number;

    if (bleed) {
      // Scale to ensure the image fully covers the page, overlapping as necessary
      const widthRatio = pageWidthInPoints / originalWidth;
      const heightRatio = pageHeightInPoints / originalHeight;
      scale = Math.max(widthRatio, heightRatio);
      scaledWidth = originalWidth * scale;
      scaledHeight = originalHeight * scale;
      xOffset = (pageWidthInPoints - scaledWidth) / 2;
      yOffset = (pageHeightInPoints - scaledHeight) / 2;
    } else {
      // Scale to fit within margins
      const widthRatio = contentWidth / originalWidth;
      const heightRatio = contentHeight / originalHeight;
      scale = Math.min(widthRatio, heightRatio);
      scaledWidth = originalWidth * scale;
      scaledHeight = originalHeight * scale;
      xOffset = (contentWidth - scaledWidth) / 2 + margin;
      yOffset = (contentHeight - scaledHeight) / 2 + margin;
    }

    const page = pdfDoc.addPage([pageWidthInPoints, pageHeightInPoints]);
    page.drawImage(embeddedImage, {
      x: xOffset,
      y: yOffset,
      width: scaledWidth,
      height: scaledHeight,
    });
  };
}

export default withStreamlitConnection(PDFComponent);
