import React, { useEffect } from "react";
import { withStreamlitConnection } from "streamlit-component-lib";

const ScrollIframeIntoViewComponent: React.FC = () => {
  useEffect(() => {
    const scrollIframeIntoView = () => {
      // Attempt to scroll the iframe's parent document into view
      setTimeout(() => {
        try {
          const iframes = window.parent?.document.getElementsByTagName("iframe");
          for (const iframe of Array.from(iframes)) {
            if (iframe.contentWindow === window) {
              iframe.scrollIntoView({ behavior: "smooth" });
              break;
            }
          }
        } catch (error) {
          console.warn("Could not scroll iframe into view:", error);
        }
      }, 2000); // Delay for 2 seconds
    };

    scrollIframeIntoView();
  }, []);

  return (
    <div style={{ padding: "20px", background: "#f0f0f0", border: "1px solid #ccc" }}>
      <p>This iframe scrolled the parent window to its location!</p>
    </div>
  );
};

export default withStreamlitConnection(ScrollIframeIntoViewComponent);
