import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";
import React, { ReactNode } from "react";
import JSZip from "jszip";
import { PDFDocument } from "pdf-lib"; // Import pdf-lib for PDF creation

class PDFComponent extends StreamlitComponentBase {
  private isProcessing: boolean = false; // Guard to prevent double execution
  private lastProcessedImageData: string | null = null; // Store the last processed image data
  private lastProcessedFileName: string | null = null; // Store the last processed file name

  public render = (): ReactNode => {
    // Retrieve the data from the props
    const imageData = this.props.args["imageData"] as string;
    const fileName = this.props.args["fileName"] || "output.pdf";
    const imageList = this.props.args["imageList"] as Array<{ imageData: string; fileName: string }>;
    const dpi = this.props.args["dpi"] || 300; // DPI value
    const pageSize = this.props.args["pageSize"] || { width: 8.5, height: 11 }; // Page size in inches

    // Handle single or batch processing
    if (!this.isProcessing) {
      if (imageData) {
        // Check if the input is the same as the last processed one
        if (imageData !== this.lastProcessedImageData || fileName !== this.lastProcessedFileName) {
          this.isProcessing = true;
          this.lastProcessedImageData = imageData; // Update the last processed image data
          this.lastProcessedFileName = fileName; // Update the last processed file name
          this.convertSingleImageToPdf(imageData, fileName, dpi, pageSize);
        }
      } else if (imageList && imageList.length > 0) {
        this.isProcessing = true;
        this.convertImagesToPdfAndDownload(imageList, dpi, pageSize);
      }
    }

    // Return an empty div or nothing if no message needs to be displayed
    return <div></div>;
  };

  private convertSingleImageToPdf = async (
    imageData: string,
    fileName: string,
    dpi: number,
    pageSize: { width: number; height: number }
  ): Promise<void> => {
    try {
      const pdfDoc = await PDFDocument.create();
      const imageBytes = Buffer.from(imageData, "base64");
      const embeddedImage = await pdfDoc.embedPng(imageBytes); // Embed the image (supports PNG or JPG)

      // Page size in points
      const pageWidthInPoints = pageSize.width * 72;
      const pageHeightInPoints = pageSize.height * 72;

      // Original image dimensions
      const originalWidth = embeddedImage.width;
      const originalHeight = embeddedImage.height;

      // Calculate image dimensions in points based on DPI
      const imageWidthInPoints = (originalWidth / dpi) * 72;
      const imageHeightInPoints = (originalHeight / dpi) * 72;

      // Add a page with the specified dimensions
      const page = pdfDoc.addPage([pageWidthInPoints, pageHeightInPoints]);

      // Center the image on the page
      const xOffset = (pageWidthInPoints - imageWidthInPoints) / 2;
      const yOffset = (pageHeightInPoints - imageHeightInPoints) / 2;

      page.drawImage(embeddedImage, {
        x: xOffset,
        y: yOffset,
        width: imageWidthInPoints,
        height: imageHeightInPoints,
      });

      const pdfBytes = await pdfDoc.save();
      const blob = new Blob([pdfBytes], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);

      const link = document.createElement("a");
      link.href = url;
      link.download = fileName;
      document.body.appendChild(link);

      link.click(); // Immediate download for single PDF
      document.body.removeChild(link);

      Streamlit.setComponentValue(true); // Indicate success
    } catch (error) {
      console.error("Error generating PDF:", error);
      Streamlit.setComponentValue(false); // Indicate failure
    } finally {
      this.isProcessing = false; // Reset processing flag
    }
  };

  private convertImagesToPdfAndDownload = async (
    imageList: Array<{ imageData: string; fileName: string }>,
    dpi: number,
    pageSize: { width: number; height: number }
  ): Promise<void> => {
    try {
      const zip = new JSZip();

      const pdfPromises = imageList.map(async ({ imageData, fileName }) => {
        const pdfDoc = await PDFDocument.create();
        const imageBytes = Buffer.from(imageData, "base64");
        const embeddedImage = await pdfDoc.embedPng(imageBytes);

        // Page size in points
        const pageWidthInPoints = pageSize.width * 72;
        const pageHeightInPoints = pageSize.height * 72;

        // Original image dimensions
        const originalWidth = embeddedImage.width;
        const originalHeight = embeddedImage.height;

        // Calculate image dimensions in points based on DPI
        const imageWidthInPoints = (originalWidth / dpi) * 72;
        const imageHeightInPoints = (originalHeight / dpi) * 72;

        // Add a page with the specified dimensions
        const page = pdfDoc.addPage([pageWidthInPoints, pageHeightInPoints]);

        // Center the image on the page
        const xOffset = (pageWidthInPoints - imageWidthInPoints) / 2;
        const yOffset = (pageHeightInPoints - imageHeightInPoints) / 2;

        page.drawImage(embeddedImage, {
          x: xOffset,
          y: yOffset,
          width: imageWidthInPoints,
          height: imageHeightInPoints,
        });

        const pdfBytes = await pdfDoc.save();
        zip.file(`${fileName}.pdf`, pdfBytes); // Add PDF to ZIP
      });

      await Promise.all(pdfPromises);
      const zipFile = await zip.generateAsync({ type: "blob" });

      const link = document.createElement("a");
      link.href = URL.createObjectURL(zipFile);
      link.download = "images.zip";
      document.body.appendChild(link);

      link.click(); // Download ZIP
      document.body.removeChild(link);

      Streamlit.setComponentValue(true); // Indicate success
    } catch (error) {
      console.error("Error generating ZIP file:", error);
      Streamlit.setComponentValue(false); // Indicate failure
    } finally {
      this.isProcessing = false; // Reset processing flag
    }
  };
}

// Export the component
export default withStreamlitConnection(PDFComponent);
