import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib";
import React, { ReactNode } from "react";
import { PDFDocument } from "pdf-lib";

interface State {
  isProcessing: boolean;
  totalPages: number;
  processedPages: number;
}

interface Args {
  imageData?: string;
  imageList?: Array<{ imageData: string; fileName: string }>;
  fileName?: string;
  dpi?: number;
  pageSize?: { width: number; height: number };
  bleed?: boolean;
  outputFileName?: string;
  insertBlankPages?: boolean; // Option to insert blank pages
  margin?: number; // Minimum margin around the content
  gutterMargin?: number; // Gutter margin for odd/even pages
  JPG?: boolean; // Whether the image is JPG
}

class PDFComponent extends StreamlitComponentBase<State> {
  private lastProcessedImageList: string | null = null;
  private lastProcessedSingleImage: string | null = null;

  constructor(props: any) {
    super(props);
    this.state = {
      isProcessing: false,
      totalPages: 0,
      processedPages: 0,
    };
  }

  public render = (): ReactNode => {
    const args = this.props.args as Args;
    const imageData = args.imageData;
    const fileName = args.fileName || "output.pdf";
    const imageList = args.imageList;
    const dpi = args.dpi || 300;
    const pageSize = args.pageSize || { width: 8.5, height: 11 };
    const bleed = args.bleed || false;
    const insertBlankPages = args.insertBlankPages || false;
    const margin = args.margin || 0.75; // Default 0.75 inches
    const gutterMargin = args.gutterMargin || 0.0; // Default 0.0 inches
    const isJpg = args.JPG || false; // Default to PNG

    if (!this.state.isProcessing) {
      if (imageData) {
        if (imageData !== this.lastProcessedSingleImage) {
          this.lastProcessedSingleImage = imageData;
          this.setState({ isProcessing: true, totalPages: 1, processedPages: 0 }, () => {
            this.convertSingleImageToPdf(imageData, fileName, dpi, pageSize, bleed, margin, gutterMargin, isJpg, 0);
          });
        }
      } else if (imageList && imageList.length > 0) {
        const serializedImageList = JSON.stringify(imageList);
        if (serializedImageList !== this.lastProcessedImageList) {
          this.lastProcessedImageList = serializedImageList;
          const totalPages = imageList.length + (insertBlankPages ? imageList.length - 1 : 0);
          this.setState({ isProcessing: true, totalPages, processedPages: 0 }, () => {
            this.convertImagesToSinglePdf(imageList, dpi, pageSize, bleed, insertBlankPages, margin, gutterMargin, isJpg);
          });
        }
      }
    }

    return (
      <div>
        {this.state.isProcessing && (
          <>
            <progress
              value={this.state.processedPages}
              max={this.state.totalPages}
              style={{ width: "100%" }}
            ></progress>
            <p>
              Currently processing page {this.state.processedPages} of {this.state.totalPages}
            </p>
          </>
        )}
      </div>
    );
  };

  private convertSingleImageToPdf = async (
    imageData: string,
    fileName: string,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean,
    margin: number,
    gutterMargin: number,
    isJpg: boolean,
    pageIndex: number
  ): Promise<void> => {
    try {
      const pdfDoc = await PDFDocument.create();
      await this.addImageToPdf(pdfDoc, imageData, dpi, pageSize, bleed, margin, gutterMargin, isJpg, pageIndex);
      this.setState({ processedPages: 1 });
      const pdfBytes = await pdfDoc.save();
      this.triggerDownload(pdfBytes, fileName);
      Streamlit.setComponentValue(true);
    } catch (error) {
      console.error("Error generating single PDF:", error);
      Streamlit.setComponentValue(false);
    } finally {
      this.setState({ isProcessing: false });
    }
  };

  private convertImagesToSinglePdf = (
    imageList: Array<{ imageData: string; fileName: string }>,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean,
    insertBlankPages: boolean,
    margin: number,
    gutterMargin: number,
    isJpg: boolean
  ): void => {
    const pdfDocPromise = PDFDocument.create();
    let pageIndex = 0; // Track page index for gutter alignment

    const processNextImage = (index: number): void => {
      if (index >= imageList.length) {
        pdfDocPromise.then(async (pdfDoc) => {
          const pdfBytes = await pdfDoc.save();
          const fileName = (this.props.args as Args).outputFileName || "bulk_images.pdf";
          this.triggerDownload(pdfBytes, fileName);
          this.setState({ isProcessing: false });
          Streamlit.setComponentValue(true);
        });
        return;
      }

      pdfDocPromise.then(async (pdfDoc) => {
        const { imageData } = imageList[index];
        await this.addImageToPdf(pdfDoc, imageData, dpi, pageSize, bleed, margin, gutterMargin, isJpg, pageIndex);
        pageIndex++; // Increment pageIndex after each page

        if (insertBlankPages && index < imageList.length - 1) {
          const pageWidthInPoints = pageSize.width * 72;
          const pageHeightInPoints = pageSize.height * 72;
          const gutterAdjustment = pageIndex % 2 === 0
            ? gutterMargin * 72 / 2
            : -gutterMargin * 72 / 2;

          const blankPage = pdfDoc.addPage([pageWidthInPoints, pageHeightInPoints]);
          blankPage.drawRectangle({
            x: gutterAdjustment,
            y: 0,
            width: pageWidthInPoints,
            height: pageHeightInPoints,
            color: undefined,
          });
          pageIndex++;
        }

        this.setState((prevState) => ({
          processedPages: prevState.processedPages + 1,
        }));

        setTimeout(() => processNextImage(index + 1), 0);
      });
    };

    processNextImage(0);
  };

  private addImageToPdf = async (
    pdfDoc: PDFDocument,
    imageData: string,
    dpi: number,
    pageSize: { width: number; height: number },
    bleed: boolean,
    margin: number,
    gutterMargin: number,
    isJpg: boolean,
    pageIndex: number
  ): Promise<void> => {
    const imageBytes = Uint8Array.from(atob(imageData), (c) => c.charCodeAt(0));
    const embeddedImage = isJpg
      ? await pdfDoc.embedJpg(imageBytes)
      : await pdfDoc.embedPng(imageBytes);

    const pageWidthInPoints = pageSize.width * 72;
    const pageHeightInPoints = pageSize.height * 72;

    const adjustedMargin = margin * 72;
    const gutterAdjustment = pageIndex % 2 === 0
      ? gutterMargin * 72 / 2
      : -gutterMargin * 72 / 2;

    const widthRatio = (pageWidthInPoints - 2 * adjustedMargin) / embeddedImage.width;
    const heightRatio = (pageHeightInPoints - 2 * adjustedMargin) / embeddedImage.height;
    const scale = bleed ? Math.max(widthRatio, heightRatio) : Math.min(widthRatio, heightRatio);

    const scaledWidth = embeddedImage.width * scale;
    const scaledHeight = embeddedImage.height * scale;
    const xOffset = adjustedMargin + gutterAdjustment + (pageWidthInPoints - scaledWidth - 2 * adjustedMargin) / 2;
    const yOffset = adjustedMargin + (pageHeightInPoints - scaledHeight - 2 * adjustedMargin) / 2;

    const page = pdfDoc.addPage([pageWidthInPoints, pageHeightInPoints]);
    page.drawImage(embeddedImage, {
      x: xOffset,
      y: yOffset,
      width: scaledWidth,
      height: scaledHeight,
    });
  };

  private triggerDownload = (pdfBytes: Uint8Array, fileName: string): void => {
    const blob = new Blob([pdfBytes], { type: "application/pdf" });
    const url = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = url;
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };
}

export default withStreamlitConnection(PDFComponent);
